import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

const blogForm = ({
  addBlog,
  newTitle,
  setNewTitle,
  newAuthor,
  setNewAuthor,
  newUrl,
  setNewUrl
}) => (
  <form onSubmit={addBlog}>
    <div>
      {/* Title:
      <input
        type="text"
        value={newTitle}
        onChange={({ target }) => setNewTitle(target.value)}
      /> */}
      <TextField
        value={newTitle}
        onChange={({ target }) => setNewTitle(target.value)}
        label="Title"
      />
    </div>

    <div>
      {/* Author:
      <input
        type="text"
        value={newAuthor}
        onChange={({ target }) => setNewAuthor(target.value)}
      /> */}
      <TextField
        value={newAuthor}
        onChange={({ target }) => setNewAuthor(target.value)}
        label="Author"
      />
    </div>

    <div>
      {/* URL:
      <input
        type="url"
        value={newUrl}
        onChange={({ target }) => setNewUrl(target.value)}
      /> */}
      <TextField
        value={newUrl}
        type="url"
        onChange={({ target }) => setNewUrl(target.value)}
        label="URL"
      />
    </div>

    {/* <button type="submit">Submit</button> */}
    <Button
      variant="outlined"
      color="primary"
      type="submit"
      style={{ marginTop: 10 }}
    >
      Submit
    </Button>
    <br />
    <br />
  </form>
);

export default blogForm;
